import React, { useState } from 'react';
import stlye from './QA.css'


function QAPage() {
  const [questions, setQuestions] = useState([
    {
      id: 1,
      name: 'John',
      date: '2023-07-03',
      question: 'What are the best sneakers for running?',
      answer: 'The Nike Air Zoom Pegasus and Adidas Ultraboost are highly recommended for running.',
    },
    {
      id: 2,
      name: 'Jane',
      date: '2023-07-02',
      question: 'Which brand offers the most comfortable sneakers?',
      answer: 'Many users find that New Balance and ASICS provide great comfort in their sneakers.',
    },
  ]);

  const [newQuestion, setNewQuestion] = useState({ name: '', date: '', question: '' });

  const handleInputChange = (e) => {
    setNewQuestion({ ...newQuestion, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const updatedQuestions = [...questions];
    updatedQuestions.push({ ...newQuestion, id: Date.now() });
    setQuestions(updatedQuestions);
    setNewQuestion({ name: '', date: '', question: '' });
  };

  return (
    <div>
      <h1>Q&A</h1>
      <div>
        <h2>Ask a Question</h2>
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="name">Name:</label>
            <input type="text" id="name" name="name" value={newQuestion.name} onChange={handleInputChange} />
          </div>
          <div>
            <label htmlFor="date">Date:</label>
            <input type="text" id="date" name="date" value={newQuestion.date} onChange={handleInputChange} />
          </div>
          <div>
            <label htmlFor="question">Question:</label>
            <textarea id="question" name="question" value={newQuestion.question} onChange={handleInputChange} />
          </div>
          <button type="submit">Submit</button>
        </form>
      </div>
      <div>
        <h2>Recent Questions</h2>
        {questions.map((question) => (
          <div key={question.id}>
            <p>Name: {question.name}</p>
            <p>Date: {question.date}</p>
            <p>Question: {question.question}</p>
            <p>Answer: {question.answer}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default QAPage;
