import styles from '../pages_css/Home.css';
import Navbar from '../compenents/Navbar.js';
import Footer from '../compenents/Footer.js';
import { useState } from 'react';


const Carousel = () => {
  const [activeIndex, setActiveIndex] = useState(0);

  const handlePrev = () => {
    setActiveIndex((prevIndex) => (prevIndex === 0 ? 2 : prevIndex - 1));
  };

  const handleNext = () => {
    setActiveIndex((prevIndex) => (prevIndex === 2 ? 0 : prevIndex + 1));
  };

  return (
    <div className="carousel">
      <div className="carousel-inner">
        <div className={`carousel-item ${activeIndex === 0 ? 'active' : ''}`}>
          <img src="./Image/15.jpg" alt="Image 1" />
          <div className="carousel-caption">
            <h3>Don't Blink Studio</h3>
            <p>Welcome to Don't Blink Studio, where moments are captured and memories are made eternal.</p>
          </div>
        </div>
        <div className={`carousel-item ${activeIndex === 1 ? 'active' : ''}`}>
          <img src="./Image/16.jpg" alt="Image 2" />
          <div className="carousel-caption">
            <h3>Goal</h3>
            <p>In our studio, we believe that every moment is precious and deserving of attention.</p>
          </div>
        </div>
        <div className={`carousel-item ${activeIndex === 2 ? 'active' : ''}`}>
          <img src="./Image/17.jpg" alt="Image 3" />
          <div className="carousel-caption">
            <h3>Technology</h3>
            <p>Supported by our skilled Art Director and Photo Retoucher, Sarah Shahi.</p>
          </div>
        </div>
      </div>

      <a className="carousel-control-prev" onClick={handlePrev}>
        Previous
      </a>
      <a className="carousel-control-next" onClick={handleNext}>
        Next
      </a>
    </div>
  );
};



const Home = () => {



  return (
    <div className="home">

      <Navbar />

      <div className="quick_click_3">
        <div className="container">
          <a href="#" className="navbar-link Sneakersq-link">
            <div className="text-q">Sneakers</div>
          </a>

          <a href="#" className="navbar-link Shoesq-link">
            <div className="text-q">Shoes</div>
          </a>


          <a href="#" className="navbar-link Popularq-link">
            <div className="text-q">Popular brand</div>
          </a>
        </div>
      </div>







      <Carousel />



      <Footer />










    </div>
  );
};

export default Home;
