import Navbar from '../compenents/Navbar.js';
import Footer from '../compenents/Footer.js';


import React, { useEffect, useState } from "react";
import SliderContent from "../compenents/SliderContent.js";
import Dots from "../compenents/Dots.js";
import Arrows from "../compenents/Arrows.js";
import sliderImage from "../data/slider_news.js";

const len = sliderImage.length - 1;


function New(props) {
  const [activeIndex, setActiveIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveIndex(activeIndex === len ? 0 : activeIndex + 1);
    }, 5000);
    return () => clearInterval(interval);
  }, [activeIndex]);

  const chartRef = React.createRef();

  // Chart configuration
  const chartConfig = {
    type: 'bar',
    data: {
      labels: ['Nike', 'Adidas', 'Jordan', 'Dior', 'Louis Vuitton'],
      datasets: [
        {
          label: 'Number of Sales',
          data: [2500, 1800, 1200, 800, 600],
          backgroundColor: [
            'rgba(255, 99, 132, 0.8)',
            'rgba(54, 162, 235, 0.8)',
            'rgba(255, 206, 86, 0.8)',
            'rgba(75, 192, 192, 0.8)',
            'rgba(153, 102, 255, 0.8)',
          ],
        },
      ],
    },
    options: {
      responsive: true,
      scales: {
        y: {
          beginAtZero: true,
        },
      },
    },
  };

  // Find the index of the most sold brand
  const maxSalesIndex = chartConfig.data.datasets[0].data.indexOf(
    Math.max(...chartConfig.data.datasets[0].data)
  );
  const mostSoldBrand = chartConfig.data.labels[maxSalesIndex];

  React.useEffect(() => {
    const context = chartRef.current.getContext('2d');
    new window.Chart(context, chartConfig);
  }, []);


  return (
    <>
      <Navbar />


      <div className="quick_click_3">
        <div className="container">
          <a href="#" className="navbar-link Sneakersq-link">
            <div className="text-q">Sneakers</div>
          </a>

          <a href="#" className="navbar-link Shoesq-link">
            <div className="text-q">Shoes</div>
          </a>


          <a href="#" className="navbar-link Popularq-link">
            <div className="text-q">Popular brand</div>
          </a>
        </div>
      </div>

      <div className="slider-container">
        <SliderContent activeIndex={activeIndex} sliderImage={sliderImage} />
        <Arrows
          prevSlide={() =>
            setActiveIndex(activeIndex < 1 ? len : activeIndex - 1)
          }
          nextSlide={() =>
            setActiveIndex(activeIndex === len ? 0 : activeIndex + 1)
          }
        />
        <Dots
          activeIndex={activeIndex}
          sliderImage={sliderImage}
          onclick={(activeIndex) => setActiveIndex(activeIndex)}
        />
      </div>

      <div>
        <h1>Top 5 Brand Sales Monthly</h1>
        <h2>Most Sold Brand: {mostSoldBrand}</h2>
        <Bar data={data} />
      </div>

      <Footer />
    </>
  );
}




export default New;
