import React, { useContext, useState } from 'react';
import { ProductContext } from '../compenents/ProductContext';

import styles from '../pages_css/Shoes_detail.css';
import Navbar from '../compenents/Navbar.js';
import Footer from '../compenents/Footer.js';

const Shoes_detail = () => {
  
    
  
  const colors = ['Red', 'Blue', 'Green'];
  const sizes = ['S', 'M', 'L'];

  const [selectedColor, setSelectedColor] = useState('');
  const [selectedSize, setSelectedSize] = useState('');

  const handleColorChange = (color) => {
    setSelectedColor(color);
  };

  const handleSizeChange = (size) => {
    setSelectedSize(size);
  };


  ////
  const { selectedProductInfo } = useContext(ProductContext);

  if (!selectedProductInfo) {
    return <div>Loading...</div>; // Show loading state if no product is selected yet
  }

  const { name, price, image } = selectedProductInfo;


  return (

    <div className="ShoesDetail">
      <Navbar />
      <div className="productContainer">
        <div className="productImageContainer">
          <img src={image} alt="product-img" className="productImage" />
        </div>
        <div className="productInfoContainer">
          <h2>{name}</h2>
          <p>Price: ${price}</p>

          <div className="colorSelector">
            <h4>Select Color:</h4>
            {colors.map(color => (
              <button
                key={color}
                className={`colorButton ${selectedColor === color ? 'selected' : ''}`}
                onClick={() => handleColorChange(color)}
              >
                {color}
              </button>
            ))}
          </div>

          <div className="sizeSelector">
            <h4>Select Size:</h4>
            {sizes.map(size => (
              <button
                key={size}
                className={`sizeButton ${selectedSize === size ? 'selected' : ''}`}
                onClick={() => handleSizeChange(size)}
              >
                {size}
              </button>
            ))}
          </div>

          {/* Add any additional product details or actions here */}
        </div>
      </div>
    </div>
    
  );
};

export default Shoes_detail;
