import React, { useEffect } from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import '../pages_css/About.css';

const About = () => {
  useEffect(() => {
    const handleScroll = () => {
      const navbar = document.querySelector('.stickynavbar');
      const scrolled = window.scrollY > 0;

      if (scrolled) {
        navbar.classList.add('stickynavbar--scrolled');
      } else {
        navbar.classList.remove('stickynavbar--scrolled');
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleNavLinkClick = (e) => {
    e.preventDefault();
    const target = e.target.getAttribute('href');
    const scrollContainer = document.getElementById('scrollContainer');
    const targetSection = document.querySelector(target);

    if (scrollContainer && targetSection) {
      const containerTop = scrollContainer.getBoundingClientRect().top;
      const sectionTop = targetSection.getBoundingClientRect().top;
      const offset = sectionTop - containerTop;

      window.scrollTo({
        top: offset,
        behavior: 'smooth',
      });
    }
  };

  return (
    <div id="scrollContainer">
      <div className="About">
        <Navbar />

        <div className="stickynavbar w-nav">
          <div className="navbarcontainer-5 w-container">
            <div className="navbar-content-8">
              <div className="navbar-brand-6">
                <div className="text-23">Company</div>
              </div>
              <nav role="navigation" className="navbar-menu-5 w-nav-menu">
                <a href="#overview" className="navbar-link-11 w-nav-link" onClick={handleNavLinkClick}>
                  <div className="text-23">Overview</div>
                </a>
                <a href="#leadership" className="navbar-link-12 w-nav-link" onClick={handleNavLinkClick}>
                  <div className="text-23">Leadership</div>
                </a>
                <a href="#core-values" className="navbar-link-13 w-nav-link" onClick={handleNavLinkClick}>
                  <div className="text-23">Core Values</div>
                </a>
              </nav>
            </div>
          </div>
        </div>

        {/* Rest of your code */}
        {/* ... */}

        <Footer />
      </div>
    </div>
  );
};

export default About;
