import React, { useContext } from 'react';
import { ProductContext } from '../compenents/ProductContext';

const ShoesDetail = () => {
  const { selectedProductInfo } = useContext(ProductContext);

  if (!selectedProductInfo) {
    return <div>Loading...</div>; // Show loading state if no product is selected yet
  }

  const { name, price, image } = selectedProductInfo;

  // Display default values or loading states if data is not yet available
  const defaultName = 'Product Name';
  const defaultPrice = '$0.00';
  const defaultImage = 'https://example.com/placeholder-image.jpg';

  return (
    <div>
      <h2>{name || defaultName}</h2>
      <p>Price: {price ? `$${price}` : defaultPrice}</p>
      <img src={image || defaultImage} alt='product-img' />
    </div>
  );
};

export default ShoesDetail;
